        </CardContent>
      </Card>

      {/* Popular Topics */}
      <Card>
        <CardHeader className="pb-4">
          <CardTitle className="flex items-center space-x-2">
            <Flame className="text-accent" size={20} />
            <span>{t("community.popularTopics")}</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="flex flex-wrap gap-2">
            {popularTopics.map((topic) => (
              <Button
                key={topic}
                variant="outline"
                size="sm"
                className="rounded-full hover:bg-primary/10 hover:text-primary hover:border-primary"
                data-testid={`button-topic-${topic.replace('#', '')}`}
              >
                {topic}
              </Button>
            ))}
          </div>
        </CardContent>
      </Card>

      {/* Community Guidelines */}
      <Card className="bg-muted/50">
        <CardContent className="pt-6">
          <h4 className="font-semibold mb-2">{t("community.guidelines.title")}</h4>
          <div className="space-y-1 text-sm text-muted-foreground">
            <p>â€¢ {t("community.guidelines.respectful")}</p>
            <p>â€¢ {t("community.guidelines.relevant")}</p>
            <p>â€¢ {t("community.guidelines.helpful")}</p>
            <p>â€¢ {t("community.guidelines.accurate")}</p>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/market-tab.tsx.content:
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useQuery } from "@tanstack/react-query";
import { useLanguage } from "@/hooks/use-language";
import { TrendingUp, TrendingDown, Minus, Bell, Calculator, Truck, Clock, ChartLine } from "lucide-react";

interface MarketTabProps {
  district?: string;
}

interface MarketPrice {
  id: string;
  crop: string;
  pricePerKg: string;
  district: string;
  change: string;
  trend: string;
  date: string;
}

export default function MarketTab({ district }: MarketTabProps) {
  const { t } = useLanguage();

  const { data: marketPrices = [], isLoading } = useQuery({
    queryKey: ["/api/market-prices"],
    refetchInterval: 5 * 60 * 1000, // Refetch every 5 minutes
  });

  const getTrendIcon = (trend: string) => {
    switch (trend) {
      case "up":
        return <TrendingUp className="text-green-500" size={16} />;
      case "down":
        return <TrendingDown className="text-red-500" size={16} />;
      default:
        return <Minus className="text-muted-foreground" size={16} />;
    }
  };

  const getTrendColor = (trend: string) => {
    switch (trend) {
      case "up":
        return "text-green-500";
      case "down":
        return "text-red-500";
      default:
        return "text-muted-foreground";
    }
  };

  const getCropIcon = (crop: string) => {
    const icons: Record<string, string> = {
      "Tomatoes": "ðŸ…",
      "Chilli": "ðŸŒ¶ï¸",
      "Onions": "ðŸ§…",
      "Rice": "ðŸŒ¾",
      "Coconut": "ðŸ¥¥",
    };
    return icons[crop] || "ðŸŒ±";
  };

  if (isLoading) {
    return (
      <div className="p-4 flex items-center justify-center h-full">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
          <p className="mt-2 text-muted-foreground">{t("market.loading")}</p>
        </div>
      </div>
    );
  }

  const risingPrices = (marketPrices as MarketPrice[]).filter((price: MarketPrice) => price.trend === "up");

  return (
    <div className="h-full overflow-y-auto p-4 space-y-4">
      {/* Price Alerts */}
      {risingPrices.length > 0 && (
        <Card className="border-accent bg-accent/5">
          <CardHeader className="pb-4">
            <CardTitle className="flex items-center space-x-2 text-accent">
              <Bell size={20} />
              <span>{t("market.priceAlerts")}</span>
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="bg-accent/10 border border-accent rounded-lg p-4">
              <div className="flex items-center space-x-3 mb-2">
                <TrendingUp className="text-accent" size={20} />
                <h4 className="font-semibold text-accent">
                  {t("market.pricesRising", { crop: risingPrices[0]?.crop })}
                </h4>
              </div>
              <p className="text-sm text-muted-foreground">
                {t("market.currentPrice", { 
                  price: risingPrices[0]?.pricePerKg,
                  change: risingPrices[0]?.change 
                })}
              </p>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Today's Market Prices */}
      <Card>
        <CardHeader className="pb-4">
          <CardTitle className="flex items-center space-x-2">
            <ChartLine className="text-primary" size={20} />
            <span>{t("market.todayPrices")}</span>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          {(marketPrices as MarketPrice[]).map((price: MarketPrice) => (
            <div
              key={price.id}
              className="flex items-center justify-between p-3 bg-muted rounded-lg hover:shadow-md transition-shadow"
            >
              <div className="flex items-center space-x-3">
                <div className="text-2xl">{getCropIcon(price.crop)}</div>
                <div>
                  <p className="font-semibold">{price.crop}</p>
                  <p className="text-sm text-muted-foreground">{t("market.perKg")}</p>
                </div>
              </div>
              <div className="text-right">
                <p className="font-semibold text-lg">{price.pricePerKg}</p>
                <div className={`flex items-center ${getTrendColor(price.trend)}`}>
                  {getTrendIcon(price.trend)}
                  <span className="text-sm ml-1">{price.change}</span>
                </div>
              </div>
            </div>
          ))}
        </CardContent>
      </Card>

      {/* Best Selling Times */}
      <Card>
        <CardHeader className="pb-4">
          <CardTitle className="flex items-center space-x-2">
            <Clock className="text-primary" size={20} />
            <span>{t("market.bestSellingTimes")}</span>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          <div className="bg-primary/10 p-4 rounded-lg">
            <h4 className="font-semibold text-primary mb-2">{t("market.thisWeek")}</h4>
            <p className="text-sm text-muted-foreground">
              {t("market.bestDaysVegetables")}
            </p>
          </div>
          <div className="bg-secondary/10 p-4 rounded-lg">
            <h4 className="font-semibold text-secondary mb-2">{t("market.nextMonth")}</h4>
            <p className="text-sm text-muted-foreground">
              {t("market.riceSeasonDemand")}
            </p>
          </div>
        </CardContent>
      </Card>

      {/* Quick Actions */}
      <Card>
        <CardHeader className="pb-4">
          <CardTitle>{t("market.quickActions")}</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-2 gap-3">
            <Button
              variant="outline"
              className="p-4 h-auto flex-col space-y-2 hover:bg-primary/10"
              data-testid="button-price-calculator"
            >
              <Calculator className="text-primary" size={24} />
              <span className="text-sm font-semibold">{t("market.priceCalculator")}</span>
            </Button>
            <Button
              variant="outline"
              className="p-4 h-auto flex-col space-y-2 hover:bg-accent/10"
              data-testid="button-transport-help"
            >
              <Truck className="text-accent" size={24} />
              <span className="text-sm font-semibold">{t("market.transportHelp")}</span>
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Market Insights */}
      <Card>
        <CardHeader className="pb-4">
          <CardTitle>{t("market.insights")}</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-3">
            <div className="flex items-start space-x-3 p-3 bg-muted rounded-lg">
              <TrendingUp className="text-green-500 mt-1" size={16} />
              <div>
                <h5 className="font-semibold text-sm">{t("market.demandIncreasing")}</h5>
                <p className="text-xs text-muted-foreground">
                  {t("market.organicVegetables")}
                </p>
              </div>
            </div>
            <div className="flex items-start space-x-3 p-3 bg-muted rounded-lg">
              <Clock className="text-primary mt-1" size={16} />
              <div>
                <h5 className="font-semibold text-sm">{t("market.seasonalTip")}</h5>
                <p className="text-xs text-muted-foreground">
                  {t("market.harvestTiming")}
                </p>
              </div>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/suggestions-tab.ts.content:
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { useQuery, useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useLanguage } from "@/hooks/use-language";
import { CalendarDays, Lightbulb, CheckCircle, Clock, AlertTriangle } from "lucide-react";

interface SuggestionsTabProps {
  farmerId: string;
  farmerProfile: any;
}

interface Suggestion {
  id: string;
  title: string;
  description: string;
  priority: "high" | "medium" | "low";
  category: string;
  isCompleted: boolean;
  dueDate: string;
  createdAt: string;
}

export default function SuggestionsTab({ farmerId, farmerProfile }: SuggestionsTabProps) {
  const { t } = useLanguage();

  const { data: suggestions = [], isLoading } = useQuery({
    queryKey: ["/api/suggestions", farmerId],
    enabled: !!farmerId,
  });

  const generateSuggestionsMutation = useMutation({
    mutationFn: async () => {
      const response = await apiRequest("POST", `/api/generate-suggestions/${farmerId}`, {});
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/suggestions", farmerId] });
    },
  });

  const updateSuggestionMutation = useMutation({
    mutationFn: async ({ id, updates }: { id: string; updates: Partial<Suggestion> }) => {
      const response = await apiRequest("PATCH", `/api/suggestions/${id}`, updates);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/suggestions", farmerId] });
    },
  });

  const completeSuggestion = (id: string) => {
    updateSuggestionMutation.mutate({
      id,
      updates: { isCompleted: true },
    });
  };

  const getPriorityColor = (priority: string) => {
    switch (priority) {
      case "high":
        return "bg-destructive/10 text-destructive border-destructive";
      case "medium":
        return "bg-accent/10 text-accent border-accent";
      case "low":
        return "bg-primary/10 text-primary border-primary";
      default:
        return "bg-muted text-muted-foreground";
    }
  };

  const getPriorityIcon = (priority: string) => {
    switch (priority) {
      case "high":
        return <AlertTriangle size={16} />;
      case "medium":
        return <Clock size={16} />;
      case "low":
        return <Lightbulb size={16} />;
      default:
        return <Lightbulb size={16} />;
    }
  };

  const todayTasks = (suggestions as Suggestion[]).filter((s: Suggestion) => 
    !s.isCompleted && new Date(s.dueDate) <= new Date(Date.now() + 24 * 60 * 60 * 1000)
  );

  const upcomingTasks = (suggestions as Suggestion[]).filter((s: Suggestion) => 
    !s.isCompleted && new Date(s.dueDate) > new Date(Date.now() + 24 * 60 * 60 * 1000)
  );

  if (isLoading) {
    return (
      <div className="p-4 flex items-center justify-center h-full">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto"></div>
          <p className="mt-2 text-muted-foreground">{t("suggestions.loading")}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="h-full overflow-y-auto p-4 space-y-4">
      {/* Today's Tasks */}
      <Card>
        <CardHeader className="pb-4">
          <CardTitle className="flex items-center space-x-2 text-lg">
            <CalendarDays className="text-primary" size={20} />
            <span>{t("suggestions.todayTasks")}</span>
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-3">
          {todayTasks.length === 0 ? (
            <div className="text-center py-6">
              <CheckCircle className="mx-auto text-primary mb-2" size={32} />
              <p className="text-muted-foreground">{t("suggestions.noTasksToday")}</p>
            </div>
          ) : (
            todayTasks.map((suggestion: Suggestion) => (
              <div
                key={suggestion.id}
                className="suggestion-card border border-border rounded-lg p-4 hover:shadow-md transition-all"
              >
                <div className="flex items-start justify-between mb-3">
                  <div className="flex-1">
                    <h4 className="font-semibold text-foreground">{suggestion.title}</h4>
                    <p className="text-sm text-muted-foreground mt-1">{suggestion.description}</p>
                  </div>
                  <Badge className={getPriorityColor(suggestion.priority)} variant="outline">
                    <div className="flex items-center space-x-1">
                      {getPriorityIcon(suggestion.priority)}
                      <span className="capitalize">{suggestion.priority}</span>
                    </div>
                  </Badge>
                </div>
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-2 text-xs text-muted-foreground">
